import { useState } from 'react'

export default function QueryBuilder({ query, setQuery, compact = false }) {
  const [input, setInput] = useState(query)

  const update = () => {
    setQuery(input)
  }

  return (
    <div className={`query-bar ${compact ? 'compact' : ''}`}>
      <input
        type="text"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        onKeyDown={(e) => e.key === 'Enter' && update()}
        placeholder="Search..."
      />
      {!compact && (
        <button onClick={update}>Search</button>
      )}
    </div>
  )
}


